import React , {Component} from "react";
import {Rov,Coli,Container,Button, Row} from "react-bootstrap";
import Config from "../scripts/config";
import * as Three from "three";
class RobotState extends Component {
    state={
        ros: null,
        x:0,
        y:0,
        orientation:0,
        linear_velocity:0,
        angular_velocity:0,
    };

    constructor(){
        super();
        this.init_connection();
    }
    init_connection() {
        this.state.ros= new window.ROSLIB.Ros();
        // this.setState(prevState => ({
        //     ros:new window.ROSLIB.Ros()
        // }));
        console.log(this.state.ros);

        this.state.ros.on("connection", ()=>{
            console.log("conneciton established in Teleoperation Component!");
            console.log(this.state.ros);
            // this.state.connected=true;
            this.setState({connected: true});
        });

        this.state.ros.on("close",()=>{
            console.log("connection is closed!");
            this.setState({connected: false});

            //try to reconnect every 3 seconds
            setTimeout(()=> { 
                try{
                    this.state.ros.connect("ws://"+Config.ROSBRIGDE_SERVER_IP+
                    ":"+
                    Config.ROSBRIGDE_SERVER_PORT);
                }   catch(error){
                    console.log("connection problem!");
                }
             }, Config.RECONNECTION_TIMER);
     });

         try{
            this.state.ros.connect("ws:"+Config.ROSBRIGDE_SERVER_IP+
            ":"+
            Config.ROSBRIGDE_SERVER_PORT);
        }   catch(error){
            console.log("ws://"+Config.ROSBRIGDE_SERVER_IP+
            ":"+
            Config.ROSBRIGDE_SERVER_PORT);
            console.log("connection problem!");
        }
    }
    //execute to getRobotState
    componentDidMount(){
        this.getRobotState();
    }

    getRobotState() {
        //create a pose subscriber
        pose_subscriber=new window.ROSLIB.Topic({
            ros: this.state.ros,
            name: Config.POSE_TOPIC,
            messageType:"geometry_msgs/PoseWithCovarianceStamped",  //rostopic info amcl_pose
        });

        //create  a pose callback
        pose_subscriber.subscribe((message) => {
            this.setState({x: message.pose.pose.position.x.toFixed(2)}); //   Fİxed():two numeral from after comma
            this.setState({y: message.pose.pose.position.y.toFixed(2)});
            this.setState({
                orientation: this.getOrientationFromQuaternion(
                    message.pose.pose.orientation).toFixed(2),
                });
        });

        //create a subsicrıber for the velocites in the odom topic
        var velocity_subscriber= new window.ROSLIB.Topic({
            ros: this.state.ros,
            name: Config.ODOM_TOPIC,
            messageType:"nav_msgs/Odometry",  //rostopic info odom
        });

        //callback function for the odom
        velocity_subscriber.subscribe((message) => {
            this.setState({linear_velocity: message.twist.twist.linear.x.toFixed(2)}); //   Fİxed():two numeral from after comma
            this.setState({angular_velocity: message.twist.twist.angular.z.toFixed(2)});
        });

        }
    getOrientationFromQuaternion(ros_orientation_quaternion){
        var q= new Three.Quaternion(
            ros_orientation_quaternion.x,
            ros_orientation_quaternion.y,
            ros_orientation_quaternion.z,
            ros_orientation_quaternion.w,

            );
            // convert this querternion into Roll, Pitch and Yaq
            var RPY=new Three.Euler().setFromQuaternion(q);

            return RPY["_z"]*(180/Math.PI);


    }

    render() {
        return(
            <div>
    <Row>
            <col>
            <h4 className="mt-4"> Position </h4>
            <p className="mt-0">x: {this.state.x}</p>
            <p className="mt-0">y: {this.state.y}</p>
            <p className="mt-0">:Orientation {this.state.orientation} </p>
            </col>
    </Row>
            <Row>
            <col>
            <h4 className="mt-4"> Velocities </h4>
            <p className="mt-0"> Angular Velecities:
                 {this.state.linear_velocity}</p>
            <p className="mt-0">y: Linear Velecities:
                {this.state.angular_velocity}</p>
            </col>             
            </Row>
            </div>
        );
    }
}

export default RobotState;
