// --->The structure of the HandleMove<----
import React ,{Component} from "react";
import { Joystick } from "react-joystick-component";
import  Config  from "../scripts/config";

class Teleoparation extends Component{
    state={ros: null};

    constructor(){
        super();
        this.init_connection();

        this.handleMove=this.handleMove.bind(this);
        this.handleStop=this.handleStop.bind(this);

    }

init_connection() {
    this.state.ros= newwindow.ROSLIB.Ros();
    console.log(this.state.ros);

    this.state.ros.on("connection", ()=>{
        console.log("conneciton established in Teleoperation Component!");
        console.log(this.state.ros);
        // this.state.connected=true;
        this.setState({connected: true});
    });

    this.state.ros.on("close",()=>{
        console.log("connection is closed!");
        this.setState({connected: false});

        //try to reconnect every 3 seconds
        setTimeout(()=> { 
            try{
                this.state.ros.connect("ws://"+Config.ROSBRIGDE_SERVER_IP+
                ":"+
                Config.ROSBRIGDE_SERVER_PORT);
            }   catch(error){
                console.log("connection problem!");
            }
         }, Config.RECONNECTION_TIMER);
 });

     try{
        this.state.ros.connect("ws:"+Config.ROSBRIGDE_SERVER_IP+
        ":"+
        Config.ROSBRIGDE_SERVER_PORT);
    }   catch(error){
        console.log("ws://"+Config.ROSBRIGDE_SERVER_IP+
        ":"+
        Config.ROSBRIGDE_SERVER_PORT);
        console.log("connection problem!");
    }
}
    handleMove(event){
        console.log("handle move");
        //we need to create a ros publisher on the topic cmd_vel
        var cmd_vel=new window.ROSLIB.Topic({
            ros: this.state.ros,
            name: Config.CMD_VEL_TOPIC,
            messageType: "geometry_msgs/Twist",
        });
        //we need to create a twist message to be published to rosbridge
        var twist= new window.ROSLIB.Message({
            linear:{
                // velocity settings with "event"
                x:event.y/100,
                y:0,
                z:0,
            },
            angular:{
                x:0,
                y:0,
                z:~event.x/100,
            },
        });
        //we need to publish the message on the cmd_vel topic
        cmd_vel.publish(twist);
    }

    handleStop(event){
        console.log("handle stop");
        //we need to create a ros publisher on the topic cmd_vel
        var cmd_vel=new window.ROSLIB.Topic({
            ros: this.state.ros,
            name: Config.CMD_VEL_TOPIC,
            messageType: "geometry_msgs/Twist",
        });
        //we need to create a twist message to be published to rosbridge
        var twist= new window.ROSLIB.Message({
            linear:{
                // velocity settings with "event"
                x: event.y/100,
                y:0,
                z:0,
            },
            angular:{
                x:0,
                y:0,
                z:0,
            },
        });
        //we need to publish the message on the cmd_vel topic
        cmd_vel.publish(twist);

    }

    render(){
        // return (<h3>Teleoparation</h3>);
        return (<div>
            <Joystick>
                size={100}
                sticky={true} 
                baseColor="#EEEEEE"
                stickColor="#BBBBBB"
                move={this.handleMove} 
                stop={this.handleStop}
            </Joystick>
        </div>);

    }
}
export default Teleoparation;
