import React , {Component} from "react";
import {Rov,Coli,Container,Button, Row} from "react-bootstrap";
import Config from "../scripts/config";
class RobotState extends Component {
    state={
        ros: null,
        x:0,
        y:0,
        orientation:0,
        linear_velocity:0,
        angular_velocity:0,
    };

    constructor(){
        super();
        this.init_connection();
    }
    init_connection() {
        this.state.ros= new window.ROSLIB.Ros();
        // this.setState(prevState => ({
        //     ros:new window.ROSLIB.Ros()
        // }));
        console.log(this.state.ros);

        this.state.ros.on("connection", ()=>{
            console.log("conneciton established in Teleoperation Component!");
            console.log(this.state.ros);
            // this.state.connected=true;
            this.setState({connected: true});
        });

        this.state.ros.on("close",()=>{
            console.log("connection is closed!");
            this.setState({connected: false});

            //try to reconnect every 3 seconds
            setTimeout(()=> { 
                try{
                    this.state.ros.connect("ws://"+Config.ROSBRIGDE_SERVER_IP+
                    ":"+
                    Config.ROSBRIGDE_SERVER_PORT);
                }   catch(error){
                    console.log("connection problem!");
                }
             }, Config.RECONNECTION_TIMER);
     });

         try{
            this.state.ros.connect("ws:"+Config.ROSBRIGDE_SERVER_IP+
            ":"+
            Config.ROSBRIGDE_SERVER_PORT);
        }   catch(error){
            console.log("ws://"+Config.ROSBRIGDE_SERVER_IP+
            ":"+
            Config.ROSBRIGDE_SERVER_PORT);
            console.log("connection problem!");
        }
    }
    //execute to getRobotState
    componentDidMount(){
        this.getRobotState();
    }

    getRobotState() {
        //create a pose subscriber
        pose_subscriber=new window.ROSLIB.Topic({
            ros: this.state.ros,
            name:"/amcl_pose",
            messageType:"geometry_msgs/PoseWithCovarianceStamped",  //rostopic info amcl_pose
        });

        //create  a pose callback
        pose_subscriber.subscribe((message) => {
            this.setState({x: message.pose.pose.position.x.toFixed(2)}); //  two numeral from after comma
            this.setState({y: message.pose.pose.position.y.toFixed(2)});
        });
    }
    render() {
        return(
            <div>
    <Row>
            <col>
            <h4 className="mt-4"> Position </h4>
            <p className="mt-0">x: {this.state.x}</p>
            <p className="mt-0">y: {this.state.y}</p>
            <p className="mt-0">:Orientation {this.state.orientation} </p>
            </col>
    </Row>
            <Row>
            <col>
            <h4 className="mt-4"> Velocities </h4>
            <p className="mt-0"> Angular Velecities:
                 {this.state.linear_velocity}</p>
            <p className="mt-0">y: Linear Velecities:
                {this.state.angular_velocity}</p>
            </col>             
            </Row>
            </div>
        );
    }
}

export default RobotState;
