import React, { Component } from 'react';

class WaypointForm extends Component {

  constructor(props) {
    super(props);
    this.state = {
      x: 0,
      y: 0,
      z: 0,
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: parseFloat(value) }); // x, y ve z değerlerini float'a çeviriyor.
  };

  handleSubmit = (event) => {
    event.preventDefault();//burada veri kaydedilip işlenirken yani ROS ile iletişim sağlanırken sayfanın yenilenmesi engelleniyor.
    const { x, y, z } = this.state;//altta kaydedilen veriler x y z olarak alınıyor.
    // Twist mesajını oluşturulup ve ROS'a gönderiliyor.
    const twist = new window.ROSLIB.Message({
      linear: {
        x: x,
        y: y,
        z: z,
      },
      angular: {
        x: 0,
        y: 0,
        z: 0,
      },
    });
    const cmd_vel = new window.ROSLIB.Topic({
      ros: this.props.ros,
      name: "cmd_vel", // "cmd_vel" topic'i adını kullanın
      messageType: "geometry_msgs/Twist",
    });
    cmd_vel.publish(twist);
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          X:
          <input
            type="number"
            name="x"
            value={this.state.x}
            onChange={this.handleChange}
          />
        </label>
        <label>
          Y:
          <input
            type="number"
            name="y"
            value={this.state.y}
            onChange={this.handleChange}
          />
        </label>
        <label>
          Z:
          <input
            type="number"
            name="z"
            value={this.state.z}
            onChange={this.handleChange}
          />
        </label>
        <button type="submit">Hareket Et</button>
      </form>
    );
  }
}

export default WaypointForm;
